import React, { useState } from 'react';
import { GoogleMap, Marker, DirectionsRenderer, useJsApiLoader, Autocomplete } from '@react-google-maps/api';
import { useLocation } from 'react-router-dom';
import './DirectionsPage.css';

const DirectionsPage = () => {
  const { search } = useLocation();
  const params = new URLSearchParams(search);
  const destLat = parseFloat(params.get('lat'));
  const destLng = parseFloat(params.get('lng'));

  const [origin, setOrigin] = useState('');
  const [directions, setDirections] = useState(null);
  const [autocomplete, setAutocomplete] = useState(null);

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: "AIzaSyAgtx6yT4yO1KxGO1qRIDAbi2saRZYjwJ8",
    libraries: ['places']
  });

  const handleGetDirections = () => {
    if (!origin) return;
    const directionsService = new window.google.maps.DirectionsService();
    directionsService.route(
      {
        origin,
        destination: { lat: destLat, lng: destLng },
        travelMode: window.google.maps.TravelMode.DRIVING,
      },
      (result, status) => {
        if (status === window.google.maps.DirectionsStatus.OK) {
          setDirections(result);
        }
      }
    );
  };

  return (
    <div className="directions-page-container">
      <h2>Get Directions</h2>
      <div className="directions-form-row">
        <Autocomplete
          onLoad={ac => setAutocomplete(ac)}
          onPlaceChanged={() => {
            if (autocomplete) {
              setOrigin(autocomplete.getPlace().formatted_address);
            }
          }}
        >
          <input
            type="text"
            placeholder="From (address, place, or 'Current Location')"
            value={origin}
            onChange={e => setOrigin(e.target.value)}
          />
        </Autocomplete>
        <button onClick={handleGetDirections}>Show Route</button>
        <button
          type="button"
          onClick={() => {
            if (navigator.geolocation) {
              navigator.geolocation.getCurrentPosition(pos => {
                setOrigin(`${pos.coords.latitude},${pos.coords.longitude}`);
              });
            }
          }}
          style={{ marginLeft: 8 }}
        >
          Use Current Location
        </button>
      </div>
      <div className="directions-map-container">
        {isLoaded && (
          <GoogleMap
            mapContainerStyle={{ width: '100%', height: '100%' }}
            center={{ lat: destLat, lng: destLng }}
            zoom={13}
          >
            {/* Always show the marker for the fishing spot */}
            <Marker position={{ lat: destLat, lng: destLng }} />
            {directions && <DirectionsRenderer directions={directions} />}
          </GoogleMap>
        )}
      </div>
    </div>
  );
};

export default DirectionsPage;
