import React, { useState } from 'react';
import { GoogleMap, Marker, DirectionsRenderer, useJsApiLoader, Autocomplete } from '@react-google-maps/api';
import { useLocation, useHistory } from 'react-router-dom';
import './DirectionsPage.css';

const DirectionsPage = () => {
  const { search } = useLocation();
  const params = new URLSearchParams(search);
  const destLat = parseFloat(params.get('lat'));
  const destLng = parseFloat(params.get('lng'));

  const [origin, setOrigin] = useState('');
  const [directions, setDirections] = useState(null);
  const [autocomplete, setAutocomplete] = useState(null);
  const [routeInfo, setRouteInfo] = useState({ distance: '', duration: '' });

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: "AIzaSyAgtx6yT4yO1KxGO1qRIDAbi2saRZYjwJ8",
    libraries: ['places']
  });

  const history = useHistory();

  const handleGetDirections = () => {
    if (!origin) return;
    const directionsService = new window.google.maps.DirectionsService();
    directionsService.route(
      {
        origin,
        destination: { lat: destLat, lng: destLng },
        travelMode: window.google.maps.TravelMode.DRIVING,
      },
      (result, status) => {
        if (status === window.google.maps.DirectionsStatus.OK) {
          setDirections(result);
          // Extract distance and duration from the first route/leg
          const leg = result.routes[0]?.legs[0];
          if (leg) {
            setRouteInfo({
              distance: leg.distance.text,
              duration: leg.duration.text
            });
          }
        }
      }
    );
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #174a8c 0%, #43c6ac 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '0',
      position: 'relative',
      overflow: 'hidden',
    }}>
      <div style={{
        maxWidth: '600px',
        width: '95vw',
        margin: '40px auto',
        background: 'rgba(255,255,255,0.13)',
        borderRadius: '28px',
        boxShadow: '0 12px 48px #174a8c55',
        padding: '40px 32px 32px 32px',
        backdropFilter: 'blur(16px)',
        border: '1.5px solid #fff',
        position: 'relative',
        zIndex: 2,
      }}>
      <h2 style={{
          textAlign: 'center',
          color: '#fff',
          marginBottom: '32px',
          fontFamily: 'Montserrat, Segoe UI, Arial, sans-serif',
          fontSize: '2.1em',
          fontWeight: 900,
          letterSpacing: '1px',
          textShadow: '0 2px 12px #2176d244',
        }}>
          <span style={{fontSize: '1.2em', marginRight: 8}}>üß≠</span> Get Directions
        </h2>
        {isLoaded ? (
          <>
            <div style={{
              display: 'flex',
              gap: '12px',
              marginBottom: '22px',
              justifyContent: 'center',
              flexWrap: 'wrap',
            }}>
              <Autocomplete
                onLoad={ac => setAutocomplete(ac)}
                onPlaceChanged={() => {
                  if (autocomplete) {
                    setOrigin(autocomplete.getPlace().formatted_address);
                  }
                }}
              >
                <input
                  type="text"
                  placeholder="From (address, place, or 'Current Location')"
                  value={origin}
                  onChange={e => setOrigin(e.target.value)}
                  style={{
                    flex: 1,
                    padding: '14px 18px',
                    borderRadius: '14px',
                    border: '2px solid #2176d2',
                    fontSize: '1.15em',
                    fontFamily: 'Montserrat, Segoe UI, Arial, sans-serif',
                    background: 'rgba(255,255,255,0.92)',
                    boxShadow: '0 2px 12px #2176d211',
                    outline: 'none',
                    transition: 'border 0.2s, box-shadow 0.2s',
                  }}
                />
              </Autocomplete>
              <button
                onClick={handleGetDirections}
                style={{
                  background: 'linear-gradient(90deg, #2176d2 60%, #43c6ac 100%)',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '14px',
                  padding: '14px 28px',
                  fontSize: '1.15em',
                  fontWeight: 700,
                  cursor: 'pointer',
                  boxShadow: '0 4px 18px #2176d233',
                  transition: 'background 0.2s, transform 0.2s',
                  marginLeft: '0',
                }}
              >Show Route</button>
              <button
                type="button"
                onClick={() => {
                  if (navigator.geolocation) {
                    navigator.geolocation.getCurrentPosition(pos => {
                      setOrigin(`${pos.coords.latitude},${pos.coords.longitude}`);
                    });
                  }
                }}
                style={{
                  background: 'linear-gradient(90deg, #43c6ac 60%, #2176d2 100%)',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '14px',
                  padding: '14px 28px',
                  fontSize: '1.15em',
                  fontWeight: 700,
                  cursor: 'pointer',
                  boxShadow: '0 4px 18px #43c6ac33',
                  transition: 'background 0.2s, transform 0.2s',
                  marginLeft: '0',
                }}
              >Use Current Location</button>
            </div>
            {routeInfo.distance && routeInfo.duration && (
              <div style={{ textAlign: 'center', margin: '18px 0', fontSize: '1.18em', color: '#fff', fontWeight: 700, background: 'rgba(67,198,172,0.18)', borderRadius: '10px', padding: '10px 0', textShadow: '0 2px 12px #2176d244' }}>
                Distance: {routeInfo.distance} &nbsp; | &nbsp; ETA: {routeInfo.duration}
              </div>
            )}
            <div style={{
              width: '100%',
              height: '400px',
              marginTop: '24px',
              borderRadius: '18px',
              overflow: 'hidden',
              boxShadow: '0 4px 18px #2176d211',
              border: '1.5px solid #2176d2',
              background: 'rgba(255,255,255,0.98)',
            }}>
              <GoogleMap
                mapContainerStyle={{ width: '100%', height: '100%' }}
                center={{ lat: destLat, lng: destLng }}
                zoom={13}
              >
                <Marker position={{ lat: destLat, lng: destLng }} />
                {directions && <DirectionsRenderer directions={directions} />}
              </GoogleMap>
            </div>
          </>
        ) : <div style={{textAlign: 'center', color: '#fff', fontSize: '1.2em', fontWeight: 700}}>Loading map...</div>}
        <button
          className="back-btn"
          onClick={() => history.push('/home')}
          style={{ marginTop: 28, background: 'linear-gradient(90deg, #2176d2 60%, #43c6ac 100%)', color: '#fff', border: 'none', borderRadius: '14px', padding: '12px 32px', fontSize: '1.08em', fontWeight: 700, boxShadow: '0 2px 8px #2176d233', transition: 'background 0.2s, transform 0.2s' }}
        >
          ‚Üê Back
        </button>
      </div>
      {/* Decorative blurred circles for glassmorphism effect */}
      <div style={{
        position: 'absolute',
        top: '10%', left: '5%',
        width: '120px', height: '120px',
        background: 'rgba(33,118,210,0.18)',
        borderRadius: '50%',
        filter: 'blur(18px)',
        zIndex: 1,
      }} />
      <div style={{
        position: 'absolute',
        bottom: '8%', right: '8%',
        width: '160px', height: '160px',
        background: 'rgba(67,198,172,0.18)',
        borderRadius: '50%',
        filter: 'blur(22px)',
        zIndex: 1,
      }} />
    </div>
  );
};

export default DirectionsPage;
