import React, { useState, useEffect, useCallback } from 'react';
import { db } from '../firebase';
import FishingSpotList from '../components/FishingSpotList';
import FilterBar from '../components/FilterBar';
import './SpotSharePage.css';
import { collection, addDoc, getDocs, setDoc, doc, query, where } from "firebase/firestore";
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';
import { auth } from '../firebase';

const containerStyle = {
  width: '100%',
  height: '200px'
};

const defaultCenter = {
  lat: 3.139, // Example: Kuala Lumpur
  lng: 101.6869
};

const SpotSharePage = () => {
    const [spots, setSpots] = useState([]);
    const [filteredSpots, setFilteredSpots] = useState([]);
    const [loading, setLoading] = useState(true);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [species, setSpecies] = useState('');
    const [technique, setTechnique] = useState('');
    const [lat, setLat] = useState('');
    const [lng, setLng] = useState('');
    const [message, setMessage] = useState('');
    const [bait, setBait] = useState('');
    const [marker, setMarker] = useState(null);
    const { isLoaded } = useJsApiLoader({
        googleMapsApiKey: "AIzaSyAgtx6yT4yO1KxGO1qRIDAbi2saRZYjwJ8" // Replace with your key
    });

    useEffect(() => {
        const fetchSpots = async () => {
            const q = query(collection(db, "fishingSpots"), where("approved", "==", true));
            const querySnapshot = await getDocs(q);
            const spotsData = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
            setSpots(spotsData);
            setFilteredSpots(spotsData);
            setLoading(false);
        };

        fetchSpots();
    }, []);

    const handleFilter = (filterCriteria) => {
        const filtered = spots.filter(spot => 
            spot.species.includes(filterCriteria.species) && 
            spot.technique.includes(filterCriteria.technique)
        );
        setFilteredSpots(filtered);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (!auth.currentUser) {
                alert("You must be logged in to add a spot.");
                return;
            }
            await addDoc(collection(db, "fishingSpots"), {
                title,
                description,
                species,
                technique,
                location: { lat: parseFloat(lat), lng: parseFloat(lng) },
                approved: false,
                createdAt: new Date()
            });
            setMessage('Spot shared! Awaiting admin approval.');
            setTitle('');
            setDescription('');
            setSpecies('');
            setTechnique('');
            setLat('');
            setLng('');
        } catch (error) {
            setMessage('Error sharing spot: ' + error.message);
        }
    };

    const onMapClick = useCallback((event) => {
        setMarker({
            lat: event.latLng.lat(),
            lng: event.latLng.lng()
        });
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="share-spot-container">
            <h1>Share Your Fishing Spots</h1>
            <FilterBar onFilter={handleFilter} />
            <FishingSpotList spots={filteredSpots} />
            <div className="spot-share-page">
                <h2>Share a Fishing Spot</h2>
                {message && <p>{message}</p>}
                <form onSubmit={handleSubmit} className="spot-share-form">
                    <input
                        type="text"
                        placeholder="Title"
                        value={title}
                        onChange={e => setTitle(e.target.value)}
                        required
                    />
                    <textarea
                        placeholder="Description"
                        value={description}
                        onChange={e => setDescription(e.target.value)}
                        required
                    />
                    <input
                        type="text"
                        placeholder="Species"
                        value={species}
                        onChange={e => setSpecies(e.target.value)}
                    />
                    <input
                        type="text"
                        placeholder="Technique (e.g., casting, bottom)"
                        value={technique}
                        onChange={e => setTechnique(e.target.value)}
                    />
                    <input
                        type="number"
                        placeholder="Latitude"
                        value={lat}
                        onChange={e => setLat(e.target.value)}
                        required
                    />
                    <input
                        type="number"
                        placeholder="Longitude"
                        value={lng}
                        onChange={e => setLng(e.target.value)}
                        required
                    />
                    <button type="submit">Share Spot</button>
                </form>
            </div>
            <div className="map-section">
                {isLoaded && (
                    <GoogleMap
                        mapContainerStyle={containerStyle}
                        center={marker || defaultCenter}
                        zoom={12}
                        onClick={onMapClick}
                    >
                        {marker && <Marker position={marker} />}
                    </GoogleMap>
                )}
            </div>
            <div className="form-section">
                <div className="input-row">
                    <label>SPECIES :</label>
                    <input
                        type="text"
                        placeholder="LIST SPECIES HERE"
                        value={species}
                        onChange={e => setSpecies(e.target.value)}
                    />
                </div>
                <div className="input-row">
                    <label>BAIT/LURE :</label>
                    <input
                        type="text"
                        placeholder="LIST BAIT/LURE HERE"
                        value={bait}
                        onChange={e => setBait(e.target.value)}
                    />
                </div>
                <button className="share-btn">
                    <span role="img" aria-label="location">üìç</span> Share Location
                </button>
                {marker && (
                    <div style={{ marginTop: 10 }}>
                        <strong>Selected Location:</strong> {marker.lat.toFixed(5)}, {marker.lng.toFixed(5)}
                    </div>
                )}
            </div>
        </div>
    );
};

export default SpotSharePage;
