import React, { useEffect, useState } from 'react';
import { db } from '../firebase';
import { collection, getDocs, query, where } from "firebase/firestore";
import FishingSpotCard from './FishingSpotCard';
import './FishingSpotList.css';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';

const FishingSpotList = () => {
  const [spots, setSpots] = useState([]);
  const [search, setSearch] = useState('');
  const [fullscreenSpot, setFullscreenSpot] = useState(null);

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: "YOUR_GOOGLE_MAPS_API_KEY" // replace with your actual key
  });

  useEffect(() => {
    const fetchSpots = async () => {
      // Only fetch approved spots from Firestore
      const q = query(
        collection(db, "fishingSpots"),
        where("approved", "==", true)
      );
      const querySnapshot = await getDocs(q);
      const spotsData = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setSpots(spotsData);
    };
    fetchSpots();
  }, []);

  if (!isLoaded) {
    return <div>Loading map...</div>;
  }

  return (
    <div className="fishing-spot-list">
      <h2>Fishing Spots</h2>
      <input
        type="text"
        className="fishing-spot-search"
        placeholder="Search fishing spots..."
        value={search}
        onChange={e => setSearch(e.target.value)}
        style={{ margin: '0 auto 32px auto', display: 'block', width: '320px', padding: '10px', borderRadius: '8px', border: '1px solid #ccc', fontSize: '1.1em' }}
      />
      <div className="fishing-spot-grid">
        {spots
          .filter(spot =>
            spot.title?.toLowerCase().includes(search.toLowerCase()) ||
            spot.species?.toLowerCase().includes(search.toLowerCase()) ||
            spot.bait?.toLowerCase().includes(search.toLowerCase()) ||
            spot.state?.toLowerCase().includes(search.toLowerCase()) // <-- add this line
          )
          .map(spot => (
            <div key={spot.id} className="fishing-spot-card">
              {/* Map or image on top */}
              {spot.location && (
                <div className="fishing-spot-map">
                  <GoogleMap
                    mapContainerStyle={{ width: '100%', height: '100%', borderTopLeftRadius: '12px', borderTopRightRadius: '12px' }}
                    center={{ lat: spot.location.lat, lng: spot.location.lng }}
                    zoom={13}
                    options={{
                      disableDefaultUI: true,
                      draggable: true,
                      zoomControl: true,
                      scrollwheel: true
                    }}
                  >
                    <Marker position={{ lat: spot.location.lat, lng: spot.location.lng }} />
                  </GoogleMap>
                </div>
              )}
              {/* Info and buttons below */}
              <div className="fishing-spot-info">
                <div className="fishing-spot-title">
                  <span role="img" aria-label="location">üìç</span>
                  <strong>
                    {spot.title || spot.locationName || `${spot.location?.lat}, ${spot.location?.lng}`}
                  </strong>
                </div>
                <div className="fishing-spot-details">
                  <div><strong>Species:</strong> {spot.species || 'N/A'}</div>
                  <div><strong>Bait:</strong> {spot.bait || 'N/A'}</div>
                  <div><strong>Technique:</strong> {spot.technique || 'N/A'}</div>
                </div>
                <div className="fishing-spot-actions">
                  <button>Info</button>
                  <button>Navigate</button>
                </div>
              </div>
            </div>
          ))}
      </div>
      {fullscreenSpot && (() => {
        const spot = spots.find(s => s.id === fullscreenSpot);
        if (!spot) return null;
        return (
          <div className="fullscreen-map-overlay" onClick={() => setFullscreenSpot(null)}>
            <div className="fullscreen-map-container" onClick={e => e.stopPropagation()}>
              <button className="close-fullscreen-map" onClick={() => setFullscreenSpot(null)}>‚úï</button>
              <GoogleMap
                mapContainerStyle={{ width: '80vw', height: '70vh', borderRadius: '16px' }}
                center={{ lat: spot.location.lat, lng: spot.location.lng }}
                zoom={15}
                options={{
                  disableDefaultUI: false,
                  zoomControl: true,
                  scrollwheel: true
                }}
              >
                <Marker position={{ lat: spot.location.lat, lng: spot.location.lng }} />
              </GoogleMap>
            </div>
          </div>
        );
      })()}
    </div>
  );
};

export default FishingSpotList;
