import React from 'react';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';

const miniMapStyle = {
  width: '100%',
  height: '200px', // Increased for better control visibility
  borderTopLeftRadius: '8px',
  borderTopRightRadius: '8px'
};

const FishingSpotCard = ({ spot }) => {
  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: "AIzaSyAgtx6yT4yO1KxGO1qRIDAbi2saRZYjwJ8",
    libraries: ['places'] // <-- Make sure this matches everywhere
  });

  return (
    <div className="spot-card">
      <div className="spot-mini-map">
        {isLoaded && spot.location && (
          <GoogleMap
            mapContainerStyle={miniMapStyle}
            center={spot.location}
            zoom={15}
            options={{
              mapTypeControl: true,         // Enables Map/Satellite toggle
              streetViewControl: true,      // Enables Pegman (Street View)
              fullscreenControl: true,      // Enables fullscreen button
              zoomControl: true,            // Enables zoom buttons
              draggable: true,              // Allows dragging the map
              scrollwheel: true,            // Allows zooming with mouse wheel
              disableDefaultUI: false      // Shows all default controls
            }}
          >
            <Marker position={spot.location} />
          </GoogleMap>
        )}
      </div>
      <div className="spot-card-info">
        <div className="spot-card-row">
          <span className="spot-card-icon">‚ÑπÔ∏è</span>
          <span>Info</span>
        </div>
        <div className="spot-card-row">
          <span className="spot-card-icon">üìç</span>
          <span>Navigate</span>
        </div>
      </div>
    </div>
  );
};

export default FishingSpotCard;
