import React, { useState } from 'react';

const FilterBar = ({ onFilter }) => {
    const [species, setSpecies] = useState('');
    const [technique, setTechnique] = useState('');

    const handleChange = () => {
        onFilter({ species, technique });
    };

    return (
        <div className="filter-bar">
            <input
                type="text"
                placeholder="Species"
                value={species}
                onChange={e => { setSpecies(e.target.value); handleChange(); }}
            />
            <input
                type="text"
                placeholder="Technique"
                value={technique}
                onChange={e => { setTechnique(e.target.value); handleChange(); }}
            />
        </div>
    );
};

export default FilterBar;
