import React, { useState } from 'react';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';
import { db } from '../firebase'; // Adjust path if needed
import { doc, updateDoc, arrayUnion } from "firebase/firestore";

const miniMapStyle = {
  width: '100%',
  height: '200px', // Increased for better control visibility
  borderTopLeftRadius: '8px',
  borderTopRightRadius: '8px'
};

const FishingSpotCard = ({ spot }) => {
  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: "AIzaSyAgtx6yT4yO1KxGO1qRIDAbi2saRZYjwJ8",
    libraries: ['places'] // <-- Make sure this matches everywhere
  });

  // Calculate average rating
  const avgRating = spot.ratings && spot.ratings.length
    ? (spot.ratings.reduce((a, b) => a + b, 0) / spot.ratings.length).toFixed(1)
    : "No ratings yet";

  const [userRating, setUserRating] = useState(0);
  const [submitting, setSubmitting] = useState(false);

  const handleRatingSubmit = async () => {
    if (!userRating) return;
    setSubmitting(true);
    try {
      await updateDoc(doc(db, "fishingSpots", spot.id), {
        ratings: arrayUnion(userRating)
      });
      setUserRating(0); // Reset after submit
      // Optionally, refresh spot data here
    } catch (error) {
      console.error("Error submitting rating:", error);
    }
    setSubmitting(false);
  };

  return (
    <div className="spot-card">
      <div className="spot-mini-map">
        {isLoaded && spot.location && (
          <GoogleMap
            mapContainerStyle={miniMapStyle}
            center={spot.location}
            zoom={15}
            options={{
              mapTypeControl: true,         // Enables Map/Satellite toggle
              streetViewControl: true,      // Enables Pegman (Street View)
              fullscreenControl: true,      // Enables fullscreen button
              zoomControl: true,            // Enables zoom buttons
              draggable: true,              // Allows dragging the map
              scrollwheel: true,            // Allows zooming with mouse wheel
              disableDefaultUI: false      // Shows all default controls
            }}
          >
            <Marker position={spot.location} />
          </GoogleMap>
        )}
      </div>
      <div className="spot-card-info">
        <div className="spot-card-row">
          <span className="spot-card-icon">ℹ️</span>
          <span>Info</span>
        </div>
        <div className="spot-card-row">
          <span className="spot-card-icon">📍</span>
          <span>Navigate</span>
        </div>
        <div className="spot-rating">
          <span>Rating: {avgRating} ⭐</span>
        </div>
        <div className="spot-rating-input">
          <label htmlFor="rating">Your Rating:</label>
          <select
            id="rating"
            value={userRating}
            onChange={e => setUserRating(Number(e.target.value))}
            disabled={submitting}
          >
            <option value={0}>Select</option>
            <option value={1}>1 ⭐</option>
            <option value={2}>2 ⭐⭐</option>
            <option value={3}>3 ⭐⭐⭐</option>
            <option value={4}>4 ⭐⭐⭐⭐</option>
            <option value={5}>5 ⭐⭐⭐⭐⭐</option>
          </select>
          <button onClick={handleRatingSubmit} disabled={submitting || !userRating}>
            Submit Rating
          </button>
        </div>
      </div>
    </div>
  );
};

export default FishingSpotCard;
