import React, { useEffect, useState } from 'react';
import { getPendingSpots, approveSpot, rejectSpot } from '../firebase';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';

// 2. Set map container style
const mapContainerStyle = {
  width: '100%',
  height: '160px',
  borderRadius: '8px',
  margin: '12px 0'
};

const AdminDashboard = () => {
    const [spots, setSpots] = useState([]);

    // 3. Load Google Maps API
    const { isLoaded } = useJsApiLoader({
      googleMapsApiKey: "AIzaSyAgtx6yT4yO1KxGO1qRIDAbi2saRZYjwJ8" // <-- Replace with your actual API key
    });

    useEffect(() => {
        const fetchSpots = async () => {
            const fetchedSpots = await getPendingSpots();
            setSpots(fetchedSpots);
        };
        fetchSpots();
    }, []);

    const handleApprove = async (spotId) => {
        await approveSpot(spotId);
        setSpots(spots.filter(spot => spot.id !== spotId));
    };

    const handleReject = async (spotId) => {
        await rejectSpot(spotId);
        setSpots(spots.filter(spot => spot.id !== spotId));
    };

    return (
        <div className="admin-dashboard">
            <h1>Admin Dashboard</h1>
            <h2>Pending Fishing Spots</h2>
            <ul>
                {spots.map(spot => (
                    <li
                      key={spot.id}
                      style={{
                        marginBottom: '18px',
                        background: '#f7f6e7',
                        borderRadius: '10px',
                        padding: '16px',
                        boxShadow: '0 2px 8px #bfc6fa33',
                        display: 'flex',
                        alignItems: 'flex-start',
                        gap: '16px'
                      }}
                    >
                      <div style={{ flex: 1 }}>
                        <strong>Species:</strong> {spot.species || 'N/A'}<br />
                        <strong>Bait:</strong> {spot.bait || 'N/A'}<br />
                        <strong>Technique:</strong> {spot.technique || 'N/A'}<br />
                        <strong>Location:</strong> {spot.location ? `${spot.location.lat}, ${spot.location.lng}` : 'N/A'}<br />
                        <strong>Created By:</strong> {spot.createdBy || 'N/A'}<br />
                        <strong>Created At:</strong> {spot.createdAt ? new Date(spot.createdAt.seconds ? spot.createdAt.seconds * 1000 : spot.createdAt).toLocaleString() : 'N/A'}
                        <div style={{ marginTop: 12 }}>
                          <button onClick={() => handleApprove(spot.id)} style={{ marginRight: '8px' }}>Approve</button>
                          <button onClick={() => handleReject(spot.id)}>Reject</button>
                        </div>
                      </div>
                      {isLoaded && spot.location && (
                        <div style={{ width: 200, height: 200, minWidth: 150 }}>
                          <GoogleMap
                            mapContainerStyle={{ width: '100%', height: '100%', borderRadius: '8px' }}
                            center={{ lat: spot.location.lat, lng: spot.location.lng }}
                            zoom={13}
                            options={{
                              disableDefaultUI: true,
                              draggable: false,
                              zoomControl: false,
                              scrollwheel: false
                            }}
                          >
                            <Marker position={{ lat: spot.location.lat, lng: spot.location.lng }} />
                          </GoogleMap>
                        </div>
                      )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AdminDashboard;
