import React from 'react';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';

const miniMapStyle = {
  width: '100%',
  height: '140px',
  borderTopLeftRadius: '12px',
  borderTopRightRadius: '12px',
  overflow: 'hidden'
};

const FishingSpotCard = ({ spot, setFullscreenSpot }) => {
  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: "AIzaSyAgtx6yT4yO1KxGO1qRIDAbi2saRZYjwJ8"
  });

  return (
    <div className="spot-card">
      <div className="spot-mini-map">
        {isLoaded && spot.location && (
          <GoogleMap
            mapContainerStyle={miniMapStyle}
            center={spot.location}
            zoom={15}
            options={{ disableDefaultUI: true }}
          >
            <Marker position={spot.location} />
          </GoogleMap>
        )}
      </div>
      <div className="spot-card-info">
        <div className="spot-card-row">
          <span className="spot-card-icon">‚ÑπÔ∏è</span>
          <span>Info</span>
        </div>
        <div className="spot-card-row">
          <span className="spot-card-icon">üìç</span>
          <span>Navigate</span>
        </div>
      </div>
      <div className="fishing-spot-map">
        <button
          className="fullscreen-map-btn"
          onClick={() => setFullscreenSpot(spot.id)}
          title="View Fullscreen"
        >‚õ∂</button>
        <GoogleMap
          mapContainerStyle={{ width: '100%', height: '100%', borderTopLeftRadius: '12px', borderTopRightRadius: '12px' }}
          center={{ lat: spot.location.lat, lng: spot.location.lng }}
          zoom={13}
          options={{
            disableDefaultUI: true,
            draggable: true,
            zoomControl: true,
            scrollwheel: true
          }}
        >
          <Marker position={{ lat: spot.location.lat, lng: spot.location.lng }} />
        </GoogleMap>
      </div>
    </div>
  );
};

export default FishingSpotCard;
