import React, { useState, useCallback, useEffect } from 'react';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';
import { collection, addDoc } from "firebase/firestore";
import { db, auth } from '../firebase';
import './SpotSharePage.css';
import { useHistory } from 'react-router-dom';

const containerStyle = {
  width: '100%',
  height: '260px'
};

const defaultCenter = {
  lat: 3.139,
  lng: 101.6869
};

async function getStateFromLatLng(lat, lng) {
  const apiKey = "AIzaSyAgtx6yT4yO1KxGO1qRIDAbi2saRZYjwJ8"; // Use your actual API key
  const url = `https://maps.googleapis.com/maps/api/geocode/json?latlng=${lat},${lng}&key=${apiKey}`;
  const response = await fetch(url);
  const data = await response.json();
  console.log("Geocode API response:", data); // Debug output

  if (data.status !== "OK") {
    console.error("Geocoding error:", data.status, data.error_message);
    return "";
  }

  const stateComponent = data.results
    .flatMap(result => result.address_components)
    .find(comp => comp.types.includes('administrative_area_level_1'));

  return stateComponent ? stateComponent.long_name : '';
}

const SpotSharePage = () => {
  const [species, setSpecies] = useState('');
  const [bait, setBait] = useState('');
  const [technique, setTechnique] = useState('');
  const [marker, setMarker] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [center, setCenter] = useState(defaultCenter);
  const [spotName, setSpotName] = useState(''); // Add this line
  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: "AIzaSyAgtx6yT4yO1KxGO1qRIDAbi2saRZYjwJ8",
    libraries: ['places'] // <-- Make sure this matches everywhere
  });
  const history = useHistory();

  const onMapClick = useCallback((event) => {
    setMarker({
      lat: event.latLng.lat(),
      lng: event.latLng.lng()
    });
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!spotName.trim()) {
      alert("Please enter a spot name.");
      return;
    }
    if (!auth.currentUser) {
      alert("You must be logged in to share a spot.");
      return;
    }
    if (!marker) {
      alert("Please select a location on the map.");
      return;
    }
    setLoading(true);
    try {
      // Get the state from the marker coordinates
      const state = await getStateFromLatLng(marker.lat, marker.lng);

      await addDoc(collection(db, "fishingSpots"), {
        name: spotName,           // Add this line
        species,
        bait,
        technique,
        location: marker,
        state, // <-- Save the state here!
        approved: false,
        createdAt: new Date(),
        createdBy: auth.currentUser?.uid || 'anonymous'
      });
      setMessage('Spot shared! Awaiting admin approval.');
      setSpecies('');
      setBait('');
      setTechnique('');
      setMarker(null);
      setSpotName(''); // Clear spot name
    } catch (error) {
      setMessage('Error sharing spot: ' + error.message);
    }
    setLoading(false);
  };

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const userLocation = {
            lat: position.coords.latitude,
            lng: position.coords.longitude
          };
          setCenter(userLocation);
          setMarker(userLocation); // Set marker at user location
        },
        () => {
          setCenter(defaultCenter);
          setMarker(null); // No marker if denied
        }
      );
    }
  }, []);

  return (
    <div className="share-spot-bg">
      <div className="share-spot-card">
        <h2 className="share-title">🎣 Share a Fishing Spot</h2>
        <div className="share-map-wrap">
          {isLoaded && (
            <GoogleMap
              mapContainerStyle={containerStyle}
              center={marker || center} // <-- use the new center state
              zoom={12}
              onClick={onMapClick}
            >
              {marker && <Marker position={marker} />}
            </GoogleMap>
          )}
          <div className="selected-location">
            {marker
              ? <>📍 {marker.lat.toFixed(5)}, {marker.lng.toFixed(5)}</>
              : <>Click the map to select a location</>
            }
          </div>
        </div>
        <form className="share-form" onSubmit={handleSubmit}>
          <div className="share-fields">
            <div className="share-field">
              <label>📍 Spot Name</label>
              <input
                type="text"
                placeholder="e.g., Malacca Haruan Paradise, Sungai Petani Fishing Spot"
                value={spotName}
                onChange={e => setSpotName(e.target.value)}
                required
              />
            </div>
            <div className="share-field">
              <label>Species</label>
              <input
                type="text"
                placeholder="e.g. Peacock Bass"
                value={species}
                onChange={e => setSpecies(e.target.value)}
                required
              />
            </div>
            <div className="share-field">
              <label>Bait/Lure</label>
              <input
                type="text"
                placeholder="e.g. Soft plastic, worm"
                value={bait}
                onChange={e => setBait(e.target.value)}
                required
              />
            </div>
            <div className="share-field">
              <label>Technique</label>
              <input
                type="text"
                placeholder="e.g. Casting, bottom, fly"
                value={technique}
                onChange={e => setTechnique(e.target.value)}
                required
              />
            </div>
          </div>
          <button
            className="share-btn"
            type="submit"
          >
            Share Spot
          </button>

          <button
            className="back-btn"
            onClick={() => history.push('/home')}
            style={{ marginTop: 16 }}
          >
            ← Back
          </button>
          {message && <div className="share-message">{message}</div>}
        </form>
      </div>
    </div>
  );
};

export default SpotSharePage;
