import React, { useEffect, useState } from 'react';
import { getPendingSpots, approveSpot, rejectSpot, getApprovedSpots, deleteSpot } from '../firebase';
import { signOut } from "firebase/auth";
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';
import './AdminDashboard.css';
import { auth } from '../firebase';
import { useHistory } from 'react-router-dom';

// 2. Set map container style
const mapContainerStyle = {
  width: '100%',
  height: '160px',
  borderRadius: '8px',
  margin: '12px 0'
};

const AdminDashboard = () => {
    const [spots, setSpots] = useState([]);
    const [approvedSpots, setApprovedSpots] = useState([]);

    // 3. Load Google Maps API
    const { isLoaded } = useJsApiLoader({
      googleMapsApiKey: "AIzaSyAgtx6yT4yO1KxGO1qRIDAbi2saRZYjwJ8" // <-- Replace with your actual API key
    });

    const history = useHistory();

    useEffect(() => {
        const fetchSpots = async () => {
            const fetchedSpots = await getPendingSpots();
            setSpots(fetchedSpots);
            const fetchedApproved = await getApprovedSpots();
            setApprovedSpots(fetchedApproved);
        };
        fetchSpots();
    }, []);

    const handleApprove = async (spotId) => {
        await approveSpot(spotId);
        setSpots(spots.filter(spot => spot.id !== spotId));
    };

    const handleReject = async (spotId) => {
        await rejectSpot(spotId);
        setSpots(spots.filter(spot => spot.id !== spotId));
    };

    const handleDelete = async (spotId) => {
        await deleteSpot(spotId);
        setApprovedSpots(approvedSpots.filter(spot => spot.id !== spotId));
    };

    const handleLogout = async () => {
      await signOut(auth);
      history.push('/login');
    };

    return (
        <div className="admin-dashboard">
            <button className="admin-logout-btn" onClick={handleLogout}>Logout</button>
            <h1>Admin Dashboard</h1>
            <h2>Pending Fishing Spots</h2>
            <ul>
                {spots.map(spot => (
                    <li key={spot.id} className="admin-spot-card">
                      {/* Map on top */}
                      {isLoaded && spot.location && (
                        <div className="admin-spot-map">
                          <GoogleMap
                            mapContainerStyle={{ width: '100%', height: '100%', borderTopLeftRadius: '16px', borderTopRightRadius: '16px' }}
                            center={{ lat: spot.location.lat, lng: spot.location.lng }}
                            zoom={13}
                            options={{
                              disableDefaultUI: true,
                              draggable: true,
                              zoomControl: true,
                              scrollwheel: true
                            }}
                          >
                            <Marker position={{ lat: spot.location.lat, lng: spot.location.lng }} />
                          </GoogleMap>
                        </div>
                      )}
                      {/* Info below map */}
                      <div className="admin-spot-info">
                        <div className="admin-spot-title">
                          <span role="img" aria-label="location">üìç</span>
                          <strong>
                            {spot.title || spot.locationName || `${spot.location?.lat}, ${spot.location?.lng}`}
                          </strong>
                        </div>
                        <div className="admin-spot-details">
                          <div><strong>Species:</strong> {spot.species || 'N/A'}</div>
                          <div><strong>Bait:</strong> {spot.bait || 'N/A'}</div>
                          <div><strong>Technique:</strong> {spot.technique || 'N/A'}</div>
                          <div><strong>Created By:</strong> {spot.createdBy || 'N/A'}</div>
                          <div><strong>Created At:</strong> {spot.createdAt ? new Date(spot.createdAt.seconds ? spot.createdAt.seconds * 1000 : spot.createdAt).toLocaleString() : 'N/A'}</div>
                        </div>
                        <div className="admin-spot-actions">
                          <button onClick={() => handleApprove(spot.id)}>Approve</button>
                          <button onClick={() => handleReject(spot.id)}>Reject</button>
                        </div>
                      </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AdminDashboard;
