import React, { useState, useCallback } from 'react';
import { GoogleMap, Marker, useJsApiLoader } from '@react-google-maps/api';
import { collection, addDoc } from "firebase/firestore";
import { db, auth } from '../firebase';
import './SpotSharePage.css';

const containerStyle = {
  width: '100%',
  height: '250px'
};

const defaultCenter = {
  lat: 3.139,
  lng: 101.6869
};

const SpotSharePage = () => {
  const [species, setSpecies] = useState('');
  const [bait, setBait] = useState('');
  const [technique, setTechnique] = useState('');
  const [marker, setMarker] = useState(null);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: "AIzaSyAgtx6yT4yO1KxGO1qRIDAbi2saRZYjwJ8"
  });

  const onMapClick = useCallback((event) => {
    setMarker({
      lat: event.latLng.lat(),
      lng: event.latLng.lng()
    });
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!auth.currentUser) {
      alert("You must be logged in to share a spot.");
      return;
    }
    if (!marker) {
      alert("Please select a location on the map.");
      return;
    }
    setLoading(true);
    try {
      await addDoc(collection(db, "fishingSpots"), {
        species,
        bait,
        technique,
        location: marker,
        approved: false,
        createdBy: auth.currentUser.uid,
        createdAt: new Date()
      });
      setMessage('Spot shared! Awaiting admin approval.');
      setSpecies('');
      setBait('');
      setTechnique('');
      setMarker(null);
    } catch (error) {
      setMessage('Error sharing spot: ' + error.message);
    }
    setLoading(false);
  };

  return (
    <div className="share-spot-outer">
      <h2>Share a Fishing Spot</h2>
      <div className="map-section">
        {isLoaded && (
          <GoogleMap
            mapContainerStyle={containerStyle}
            center={marker || defaultCenter}
            zoom={12}
            onClick={onMapClick}
          >
            {marker && <Marker position={marker} />}
          </GoogleMap>
        )}
        <div className="selected-location">
          {marker
            ? <>📍 Selected Location: {marker.lat.toFixed(5)}, {marker.lng.toFixed(5)}</>
            : <>Click on the map to select a location</>
          }
        </div>
      </div>
      {message && <p>{message}</p>}
      <form className="share-spot-form" onSubmit={handleSubmit}>
        <div className="form-fields">
          <label>
            SPECIES :
            <input
              type="text"
              placeholder="LIST SPECIES HERE"
              value={species}
              onChange={e => setSpecies(e.target.value)}
              required
            />
          </label>
          <label>
            BAIT/LURE :
            <input
              type="text"
              placeholder="LIST BAIT/LURE HERE"
              value={bait}
              onChange={e => setBait(e.target.value)}
              required
            />
          </label>
          <label>
            TECHNIQUE :
            <input
              type="text"
              placeholder="e.g., casting, bottom, fly"
              value={technique}
              onChange={e => setTechnique(e.target.value)}
              required
            />
          </label>
        </div>
        <button className="share-btn" type="submit" disabled={loading}>
          {loading ? "Sharing..." : <><span role="img" aria-label="location">📍</span> Share Location</>}
        </button>
      </form>
    </div>
  );
};

export default SpotSharePage;
