import React, { useEffect, useState } from 'react';
import { getPendingSpots, approveSpot, rejectSpot } from '../firebase';

const AdminDashboard = () => {
    const [spots, setSpots] = useState([]);

    useEffect(() => {
        const fetchSpots = async () => {
            const fetchedSpots = await getPendingSpots();
            setSpots(fetchedSpots);
        };
        fetchSpots();
    }, []);

    const handleApprove = async (spotId) => {
        await approveSpot(spotId);
        setSpots(spots.filter(spot => spot.id !== spotId));
    };

    const handleReject = async (spotId) => {
        await rejectSpot(spotId);
        setSpots(spots.filter(spot => spot.id !== spotId));
    };

    return (
        <div className="admin-dashboard">
            <h1>Admin Dashboard</h1>
            <h2>Pending Fishing Spots</h2>
            <ul>
                {spots.map(spot => (
                    <li key={spot.id} style={{marginBottom: '18px', background: '#f7f6e7', borderRadius: '10px', padding: '16px', boxShadow: '0 2px 8px #bfc6fa33'}}>
                        <div>
                            <strong>Species:</strong> {spot.species || 'N/A'}<br/>
                            <strong>Bait:</strong> {spot.bait || 'N/A'}<br/>
                            <strong>Technique:</strong> {spot.technique || 'N/A'}<br/>
                            <strong>Location:</strong> {spot.location ? `${spot.location.lat}, ${spot.location.lng}` : 'N/A'}<br/>
                            <strong>Created By:</strong> {spot.createdBy || 'N/A'}<br/>
                            <strong>Created At:</strong> {spot.createdAt ? new Date(spot.createdAt.seconds ? spot.createdAt.seconds * 1000 : spot.createdAt).toLocaleString() : 'N/A'}
                        </div>
                        <button onClick={() => handleApprove(spot.id)} style={{marginRight: '8px'}}>Approve</button>
                        <button onClick={() => handleReject(spot.id)}>Reject</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AdminDashboard;
